import {useState} from "react";
import FavoritesList from './FavoritesList';

const axios = require("axios");


const Profile = () => {

    const userId = localStorage.getItem("user")
    const [favoritesList, setFavoritesList] = useState([]);

    const showFavorites = (id) => {
      console.log(favoritesList.length);
      if (favoritesList.length === 0){
        axios.get(`http://localhost:3001/favorites/${id}`)
          .then((response) => {
            console.log(response.data);
            console.log(response.data.length);
            console.log(response.data[0].playlist);

            //res.body["playlists"].items.map
            setFavoritesList(response.data["favorites"].map(favorite => {

                return {
                  name: favorite.name,
                }
              }));

              console.log(favoritesList);
          });
      }
    } 

    return (
        <div>
            <h1>{userId}</h1>
            {showFavorites(userId)}
            <FavoritesList favorites = {favoritesList}></FavoritesList>

            <div>


      </div>

        </div>

    );


}

export default Profile;
